WorkflowProcessEvent.eventDescription:
function servicetask57(attempt, message) {
	log.info(_log + " - servicetask57 - receber");
	var quais = precisaIntegrarQuaisTitulos();
	if(quais.receber) {
		integrarTitulosAReceber();
	}
	else{
		log.info(_log + " - servicetask65 - não gera títulos a pagar");
	}
}

function beforeTaskSave(colleagueId,nextSequenceId,userList){
	
	var activity = getValue("WKNumState");
	
	if(activity == Activity.ABERTURA || activity == Activity.INICIO)	
		hAPI.setCardValue("status_adiantamento","");

	
}

function regrasTitulo(){}

function precisaIntegrarQuaisTitulos(){
	log.info(_log + " - precisaIntegrarTitulo");
	
	var precisaIntegrar = { "antecipado": false, "pagar": false, "receber": false };
	//var processo = hAPI.getCardValue("rd_processo");
	var tipo = hAPI.getCardValue("rd_tipo");
	
	precisaIntegrar.antecipado = (tipo == "adiantamento");
	
	/*
	if(processo == "fornecedor" && tipo == "prestacao"){
		//XXX: Prestação de contas para fornecedor não há geração de título.
	}
	else if(processo == "fornecedor"){
		precisaIntegrar.pagar = true;
	}
	else{ 
	*/
		//viagem
		if(tipo == "adiantamento"){
			precisaIntegrar.pagar = true;
		}
		else{ //prestação de contas
			var realizado = numeroAmericano(hAPI.getCardValue("vl_realizado"));
			var total = numeroAmericano(hAPI.getCardValue("vl_adiantado"));
			
			log.info(_log + " - precisaIntegrarTitulo - realizado: " + realizado + " - total: " + total);
			
			precisaIntegrar.pagar = (realizado > 0);
			precisaIntegrar.receber = (total < 0);
		}
	//}
	
	var jaGerouAReceber = !isEmpty(hAPI.getCardValue("nr_titulo_receber"));
	if(jaGerouAReceber) precisaIntegrar.receber = false;
	
	var jaGerouAPagar = !isEmpty(hAPI.getCardValue("nr_titulo_pagar"));
	if(jaGerouAPagar) precisaIntegrar.pagar = false;

	log.info(_log + " - precisaIntegrarTitulo - jaGerouAReceber: " + jaGerouAReceber 
		+ " - jaGerouAPagar: " + jaGerouAPagar);
	log.info(_log + " - precisaIntegrarTitulo - result: ");
	log.dir(precisaIntegrar);
	return precisaIntegrar;
}

