function mobileApproval(){}

function processMobileApproval(){
	var NUM_STATE = getValue("WKNumState");
	var NEXT_STATE = getValue("WKNextState");
	var IS_MOBILE = getValue("WKMobile");
	var USER_COMMENT = getValue("WKUserComment");
	var feedback = getApprovalFeedback(NUM_STATE);
	
	log.info(_log + " - processMobileApproval "
		+ " - NUM_STATE: " + NUM_STATE + " - NEXT_STATE: " + NEXT_STATE
		+ " - IS_MOBILE: " + IS_MOBILE + " - USER_COMMENT: " + USER_COMMENT 
		+ " - feedback: ");
	log.dir(feedback);
	if(isApprovalActivity(NUM_STATE)){
		var targetStatus = approvalPath(NUM_STATE, NEXT_STATE);
		var new_feedback = { 
			"status": targetStatus, 
			"obs": isEmpty(USER_COMMENT) ? feedback.obs : USER_COMMENT
		};
		log.info(_log + " - processMobileApproval - new_feedback: ");
		log.dir(new_feedback);
		
		if((targetStatus == "Reprovado" || new_feedback.status == "Reprovado") && isEmpty(new_feedback.obs)){
			throw "Conforme o direcionamento do fluxo, o processo é aprovado ou reprovado."
				+ " Quando reprovado, o campo Observações é obrigatório."
				+ " 根據流程的方向，流程被批准或不批准。未獲批准時，備註字段為必填字段。";
		}
		setApprovalFeedback(NUM_STATE, new_feedback);
	}
}

function isApprovalActivity(currentState){
	return currentState == Activity.LANCAR_NOTA
	    || currentState == Activity.AVALIAR_FINANCEIRO
		|| currentState == Activity.AVALIAR_CONTROLADORIA
		|| currentState == Activity.AVALIAR_DIRETOR_GERAL
		|| currentState == Activity.AVALIAR_DIRETOR_FINANCEIRO
		|| currentState == Activity.AVALIAR_PRESIDENTE;	
	
}

function getApprovalFeedback(currentState){
	if(currentState == Activity.LANCAR_NOTA){
		return { 
			"status": hAPI.getCardValue("rd_lancado")
			, "obs": hAPI.getCardValue("ds_obs_lancar_nota")
		}
	}
	else if(currentState == Activity.AVALIAR_FINANCEIRO){
		return { 
			"status": hAPI.getCardValue("rd_aprov_aprovador_financeiro")
			, "obs": hAPI.getCardValue("ds_obs_aprovador_financeiro")
		}
	}
	else if(currentState == Activity.AVALIAR_CONTROLADORIA){
		return { 
			"status": hAPI.getCardValue("rd_aprov_controladoria")
			, "obs": hAPI.getCardValue("ds_obs_controladoria")
		}
	}
	else if(currentState == Activity.AVALIAR_DIRETOR_GERAL){
		return { 
			"status": hAPI.getCardValue("rd_aprov_diretor_geral")
			, "obs": hAPI.getCardValue("ds_obs_diretor_geral")
		}
	}
	else if(currentState == Activity.AVALIAR_DIRETOR_FINANCEIRO){
		return { 
			"status": hAPI.getCardValue("rd_aprov_diretor_financeiro")
			, "obs": hAPI.getCardValue("ds_obs_diretor_financeiro")
		}
	}
	else if(currentState == Activity.AVALIAR_PRESIDENTE){
		return { 
			"status": hAPI.getCardValue("rd_aprov_presidencia")
			, "obs": hAPI.getCardValue("ds_obs_presidencia")
		}
	}
	return null;
}

function setApprovalFeedback(currentState, feedback){
	if(currentState == Activity.LANCAR_NOTA){
		hAPI.setCardValue("rd_lancado", feedback.status);
		hAPI.setCardValue("ds_obs_lancar_nota", feedback.obs);
	}
	else if(currentState == Activity.AVALIAR_FINANCEIRO){
		hAPI.setCardValue("rd_aprov_aprovador_financeiro", feedback.status);
		hAPI.setCardValue("ds_obs_aprovador_financeiro", feedback.obs);
	}
	else if(currentState == Activity.AVALIAR_CONTROLADORIA){
		hAPI.setCardValue("rd_aprov_controladoria", feedback.status);
		hAPI.setCardValue("ds_obs_controladoria", feedback.obs);
	}
	else if(currentState == Activity.AVALIAR_DIRETOR_GERAL){
		hAPI.setCardValue("rd_aprov_diretor_geral", feedback.status);
		hAPI.setCardValue("ds_obs_diretor_geral", feedback.obs);
	}
	else if(currentState == Activity.AVALIAR_DIRETOR_FINANCEIRO){
		hAPI.setCardValue("rd_aprov_diretor_financeiro", feedback.status);
		hAPI.setCardValue("ds_obs_diretor_financeiro", feedback.obs);
	}
	else if(currentState == Activity.AVALIAR_PRESIDENTE){
		hAPI.setCardValue("rd_aprov_presidencia", feedback.status);
		hAPI.setCardValue("ds_obs_presidencia", feedback.obs);
	}
}

function afterTaskComplete(colleagueId,nextSequenceId,userList){
	
	var sequenceId = getValue("WKNumState");
	
	log.info("#sequenceId: "+sequenceId);
	log.info("#colleagueId: "+colleagueId);
	log.info("#nextSequenceId: "+nextSequenceId);
	log.info("#userList: "+userList);
	
	
	//Quando aprovado atualiza campos se caso usuario colocar errado
	if(sequenceId == Activity.CONFERENCIA_FINANCEIRO && nextSequenceId == Activity.AVALIAR_CONTROLADORIA){
		hAPI.setCardValue("_rd_aprov_aprovador_financeiro","Aprovado");
		hAPI.setCardValue("aux_aprov_aprovador_financeiro","Aprovado");
	}
	
	if(sequenceId == Activity.AVALIAR_CONTROLADORIA && nextSequenceId == Activity.AVALIAR_DIRETOR_GERAL){
		hAPI.setCardValue("_rd_aprov_controladoria","Aprovado");
		hAPI.setCardValue("aux_aprov_controladoria","Aprovado");
	}
	
	if(sequenceId == Activity.AVALIAR_DIRETOR_GERAL && nextSequenceId == Activity.GATEWAY_ENVOLVE_DIRETOR_FINANCEIRO){
		hAPI.setCardValue("_rd_aprov_diretor_geral","Aprovado");
		hAPI.setCardValue("aux_aprov_diretor_geral","Aprovado");
	}
	
	if(sequenceId == Activity.AVALIAR_DIRETOR_FINANCEIRO && nextSequenceId == Activity.GATEWAY_ENVOLVE_PRESIDENTE){
		hAPI.setCardValue("_rd_aprov_diretor_financeiro","Aprovado");
		hAPI.setCardValue("aux_aprov_diretor_financeiro","Aprovado");
	}
	
	if(sequenceId == Activity.AVALIAR_PRESIDENTE && nextSequenceId == Activity.DEFINIR_CONTA_PA){
		hAPI.setCardValue("_rd_aprov_presidencia","Aprovado");
		hAPI.setCardValue("aux_aprov_presidencia","Aprovado");
	}
	
	
	//Quando reprovado atualiza campos se caso usuario colocar errado
	if(sequenceId == Activity.CONFERENCIA_FINANCEIRO && nextSequenceId == Activity.GATEWAY_CORRIGIR){
		hAPI.setCardValue("_rd_aprov_aprovador_financeiro","Reprovado");
		hAPI.setCardValue("aux_aprov_aprovador_financeiro","Reprovado");
	}
	
	if(sequenceId == Activity.AVALIAR_CONTROLADORIA && nextSequenceId == Activity.GATEWAY_CORRIGIR){
		hAPI.setCardValue("_rd_aprov_controladoria","Reprovado");
		hAPI.setCardValue("aux_aprov_controladoria","Reprovado");
	}
	
	if(sequenceId == Activity.AVALIAR_DIRETOR_GERAL && nextSequenceId == Activity.GATEWAY_CORRIGIR){
		hAPI.setCardValue("_rd_aprov_diretor_geral","Reprovado");
		hAPI.setCardValue("aux_aprov_diretor_geral","Reprovado");
	}
	
	if(sequenceId == Activity.AVALIAR_DIRETOR_FINANCEIRO && nextSequenceId == Activity.GATEWAY_CORRIGIR){
		hAPI.setCardValue("_rd_aprov_diretor_financeiro","Reprovado");
		hAPI.setCardValue("aux_aprov_diretor_financeiro","Reprovado");
	}
	
	if(sequenceId == Activity.AVALIAR_PRESIDENTE && nextSequenceId == Activity.GATEWAY_CORRIGIR){
		hAPI.setCardValue("_rd_aprov_presidencia","Reprovado");
		hAPI.setCardValue("aux_aprov_presidencia","Reprovado");
	}
	
}

function approvalPath(currentState, nextState){
	if(currentState == Activity.LANCAR_NOTA){
		var map = {};
		map[Activity.GATEWAY_LANCADO] = "Sim";
		map[Activity.GATEWAY_CORRIGIR] = "Não";
		return map[nextState];
	}
	else if(currentState == Activity.AVALIAR_FINANCEIRO){
		var map = {};
		map[Activity.AVALIAR_CONTROLADORIA] = "Aprovado";
		map[Activity.GATEWAY_CORRIGIR] = "Reprovado";
		return map[nextState];
	}
	else if(currentState == Activity.AVALIAR_CONTROLADORIA){
		var map = {};
		map[Activity.AVALIAR_DIRETOR_GERAL] = "Aprovado";
		map[Activity.GATEWAY_CORRIGIR] = "Reprovado";
		return map[nextState];
	}
	else if(currentState == Activity.AVALIAR_DIRETOR_GERAL){
		var map = {};
		map[Activity.GATEWAY_ENVOLVE_DIRETOR_FINANCEIRO] = "Aprovado";
		map[Activity.GATEWAY_CORRIGIR] = "Reprovado";
		return map[nextState];
	}
	else if(currentState == Activity.AVALIAR_DIRETOR_FINANCEIRO){
		var map = {};
		map[Activity.GATEWAY_ENVOLVE_PRESIDENTE] = "Aprovado";
		map[Activity.GATEWAY_CORRIGIR] = "Reprovado";
		return map[nextState];
	}
	else if(currentState == Activity.AVALIAR_PRESIDENTE){
		var map = {};
		map[Activity.RECEBIDO_CONFERIDO_PAGO] = "Aprovado";
		map[Activity.GATEWAY_CORRIGIR] = "Reprovado";
		return map[nextState];
	}
	return null;
}

/****** 
** Enable customization script to change agreement calculation. 
** Input: 
**	stateId -> Current state, whose agreement percentage is being calculated. 
**	agreementData.get("currentPercentage") -> Current percentage, calculated by the workflow engine
**	agreementData.get("currentDestState") -> Current destination state. Zero, if process won't move
**	agreementData.get("currentDestUsers") -> Current destination users. Empty if process won't move
**/
function calculateAgreement(currentState, agreementData) {
	var NEXT_STATE = getValue("WKNextState");
	var currentDestState = agreementData.get("currentDestState"); 
	
	log.info(_log + " - calculateAgreement - currentState: " + currentState
		+ " - NEXT_STATE: " + NEXT_STATE
		+ " - currentDestState: " + currentDestState);
	//log.dir(agreementData);
	
	if(!isApprovalActivity(currentState)) return;
	
	var userList = [];
	if(isApprovalActivity(NEXT_STATE)){
		var SCOPE = {
			MECANISM: "MECANISMO", ALL: "TODOS", ALL_FLUIG: "TODOS_FLUIG"
		}
		
		var constraints = [
			DatasetFactory.createConstraint("processo", _codigoProcesso, null, ConstraintType.MUST)
			, DatasetFactory.createConstraint("solicitacao", _solicitacao, null, ConstraintType.MUST)
			, DatasetFactory.createConstraint("atividade", NEXT_STATE, null, ConstraintType.MUST)
			, DatasetFactory.createConstraint("escopo", SCOPE.MECANISM, null, ConstraintType.MUST)
		];
		var dataset = DatasetFactory.getDataset("aprovadores", null, constraints, null);
		log.info(_log + " - calculateAgreement - ds: " + dataset);
		log.dir(dataset);
		if(dataset != null && dataset.rowsCount > 0){
			for(var i = 0; i < dataset.rowsCount; i++){
				log.info(_log + " - calculateAgreement - cd_matricula "+ i + ": " + dataset.getValue(i, "cd_matricula"));
				userList.push(dataset.getValue(i, "cd_matricula"));
			}
		}
	}
	else if(NEXT_STATE == Activity.RECEBIDO_CONFERIDO_PAGO){
		userList.push("Pool:Group:financeiro");
	}
	else{
		userList.push("System:Auto");
	}
	log.info(_log + " - calculateAgreement - NEXT_STATE: " + NEXT_STATE + " - userList: " + userList.join(";"));
	agreementData.put("currentPercentage", 100); 
	agreementData.put("currentDestState", NEXT_STATE); 
	agreementData.put("currentDestUsers", userList.join(","));
}

