function isEmpty(valor){
	return valor == null || valor == '';
}

function parseOrFormatDate(date,operation,dateFormat){
	var sdf = new java.text.SimpleDateFormat(dateFormat);
	var newDate = null;
	
	if(operation == "parse")
		newDate = sdf.parse(date);
	else if(operation == "format")
		newDate = sdf.format(date);
		
	return newDate;
}

function transformDateFromTo(date,from,to){
	var dateFrom = new java.text.SimpleDateFormat(from);
	var dateTo = new java.text.SimpleDateFormat(to);
	
	return dateTo.format(dateFrom.parse(date));
}

function comprovanteAnexado(company, process, numState){
	var anexado = false;

    var hc1 = DatasetFactory.createConstraint("processHistoryPK.companyId", company, company, ConstraintType.MUST);
	var hc2 = DatasetFactory.createConstraint("processHistoryPK.processInstanceId", process, process, ConstraintType.MUST);
	var historyDataset = DatasetFactory.getDataset("processHistory", ["processHistoryPK.movementSequence", "stateSequence"], [hc1, hc2], null);
	log.info("comprovanteAnexado - HISTORY - rows - " + historyDataset.rowsCount);
	//log.dir(historyDataset);
	
	if(historyDataset.rowsCount > 0){
		var ultimoMovimento = historyDataset.getValue(historyDataset.rowsCount-1, "processHistoryPK.movementSequence");
		
		//var ultimoMovimento = historyDataset.getValue(historyDataset.rowsCount-1, "NUM_SEQ_MOVTO");
		
		var ac1 = DatasetFactory.createConstraint("processAttachmentPK.companyId", company, company, ConstraintType.MUST);
		var ac2 = DatasetFactory.createConstraint("processAttachmentPK.processInstanceId", process, process, ConstraintType.MUST);
		var ac3 = DatasetFactory.createConstraint("originalMovementSequence", ultimoMovimento, ultimoMovimento, ConstraintType.MUST);
		var attachmentDataset = DatasetFactory.getDataset("processAttachment", ["originalMovementSequence"], [ac1, ac2, ac3], null);
		log.info("QUANTOS ANEXOS? "+attachmentDataset.rowsCount);
		//log.dir(attachmentDataset);
		return attachmentDataset.rowsCount >= 1;
	}
    
    return anexado;
}

function involvesFinancialManager(){
	return involvesApproverByTrigger("gatilho_diretor_financeiro");
}
function involvesPresident(){
	return involvesApproverByTrigger("gatilho_presidente");
}

function involvesApproverByTrigger(trigger){
	//if(regraBypassEnvolvimento()) return false;

	var involves = false;
	var total = ""+hAPI.getCardValue("vl_total");	
	total = total.replaceAll(".", "");	    			
	total = parseFloat(total.replaceAll(",", ".")).toFixed(2);
	
	var target = ""+hAPI.getAdvancedProperty(trigger);
	target = target.replaceAll(".", "");
	target = parseFloat(target.replaceAll(",", ".")).toFixed(2);
	
	if(parseFloat(total) > target){
		involves = true;
	}
	return involves;
}

function regraBypassEnvolvimento(){
	return hAPI.getCardValue("cd_filial") == "1006";
}

String.prototype.replaceAll = function(from, to){ 
	var str = this.split(from).join(to); 
	return (str); 
};

function beforeSendData(customFields,customFacts){


    customFields[0] = hAPI.getCardValue("dt_pagamento").toString();    
	customFields[1] = hAPI.getCardValue("nm_centro_custo").toString();
    customFields[2] = hAPI.getCardValue("vl_total").toString();

    customFacts[0] = 999.99;

    var vl_total_aux = hAPI.getCardValue("vl_total").replace(".","").replace(".","").replace(",",".");

    customFacts[1]=java.lang.Double.parseDouble(vl_total_aux);
}

function beforeStateEntry(sequenceId){
	var attachments = hAPI.listAttachments();
	var company = ''+getValue("WKCompany");
	var process = ''+getValue("WKNumProces");
	var currentId = getValue("WKCurrentState");
  
	if (sequenceId == Activity.LANCAR_NOTA){
		var valorAnterior =  hAPI.getCardValue("vl_total");
		hAPI.setCardValue("vl_total_anterior", valorAnterior);
		
		var attchmentsNeeded = parseInt(hAPI.getAdvancedProperty("qtd_anexos"),10);
		var needBoleto = hAPI.getCardValue("sl_forma_pagamento") == "boleto";
		if(needBoleto) attchmentsNeeded++;
		
		var rateio = !isEmpty(hAPI.getCardValue("ck_rateio"));
		if(rateio) attchmentsNeeded++;
		
		var attachments = hAPI.listAttachments();
		var hasAttachment = (attachments != null && attachments.size() >= attchmentsNeeded);

		if (!hasAttachment) {
			var erro = "Atenção! 注意 ! Não esqueceça de adicionar os ("+attchmentsNeeded+") anexos";
			
			if(needBoleto)
				erro += ", incluindo o boleto. 包括匯票 "
			
			if(rateio)
				erro += ", incluindo o documento de detalhamento do rateio."
					
			erro += " 不要忘記添加附件";
					
			throw erro;
	    }
    }
    
    //Anexe o comprovante de pagamento.
	
	if(currentId == Activity.RECEBIDO_CONFERIDO_PAGO && sequenceId == Activity.GATEWAY_MAIS_PARCELAS){
		var indexes = hAPI.getChildrenIndexes("tb_parcelas");
		for (var i = 0; i < indexes.length; i++) {
			var row = indexes[i];
			var paid = hAPI.getCardValue("nm_pago___"+row);
			 log.info("#paid: "+paid);
			
			if(paid == "atual"){
				hAPI.setCardValue("nm_pago___"+row,"sim");
			}else if(paid == "pendente"){
				hAPI.setCardValue("nm_pago___"+row,"atual");
				var newPaymentDate =  hAPI.getCardValue("dt_parcela___"+row);
				hAPI.setCardValue("dt_pagamento",newPaymentDate);
				var newPaymentDateISO = transformDateFromTo(newPaymentDate,"dd/MM/yyyy","yyyy-MM-dd");
				hAPI.setCardValue("dt_pagamento_iso",newPaymentDateISO);
				break;
			}			
		}
		
		if(!comprovanteAnexado(company, process, currentId)){
			throw "Obrigatório anexar o comprovante de pagamento!";
		}
	}
	
	processMobileApproval();
}

function afterTaskCreate(colleagueId) {
	log.info("#afterTaskCreate - SEM PO");
	var sequenceId = getValue("WKCurrentState");
	
	if(sequenceId == Activity.AGUARDAR_VENCIMENTO){
		 var numProcess = getValue('WKNumProces');
		 
         var threadProcess = 0; 
         
         var dataPrazo = parseOrFormatDate(hAPI.getCardValue("dt_pagamento"),"parse","dd/MM/yyyy");
         log.info("#dataPrazo: "+dataPrazo);
         dataPrazo.setDate(dataPrazo.getDate()-7);
         log.info("#dataPrazo2: "+dataPrazo);
         var horaPrazo = (24*60*60) - 1;                            
         
         hAPI.setDueDate(numProcess, threadProcess, colleagueId, dataPrazo, horaPrazo);
	}
}
